package com.yahoo.sherlock.store.core;
import java.util.Scanner;
import java.util.concurrent.TimeUnit;
import com.yahoo.sherlock.exception.StoreException;
import com.yahoo.sherlock.settings.DatabaseConstants;
import com.yahoo.sherlock.store.StoreParams;
import io.lettuce.core.RedisClient;
import io.lettuce.core.RedisURI;
import io.lettuce.core.cluster.ClusterClientOptions;
import io.lettuce.core.cluster.ClusterTopologyRefreshOptions;
import io.lettuce.core.cluster.RedisClusterClient;
public class Client {
    private static Client client = null;

    public static Client get() {
        if (client == null) {
            client = new Client();
        }
        return client;
    }

    private RedisClient redisClient;

    private RedisClusterClient redisClusterClient;

    protected static void validateHostname(String hostname) {
        if ((hostname == null) || (hostname.length() == 0)) {
            throw new StoreException("Invalid Redis hostname: " + hostname);
        }
    }

    protected static int getPortNumber(String portStr) {
        Scanner s = new Scanner(portStr);
        int port;
        if (((!s.hasNextInt()) || ((port = s.nextInt()) < 0)) || s.hasNext()) {
            throw new StoreException("Invalid Redis port: " + portStr);
        }
        return port;
    }

    protected static int getTimeout(String timeoutStr) {
        return getPortNumber(timeoutStr);
    }

    protected static boolean getSSL(String sslStr) {
        return (null != sslStr) && (!"false".equals(sslStr.toLowerCase()));
    }

    protected static RedisURI produceURI(String hostname, String port, String ssl, String password, String timeout) {
        validateHostname(hostname);
        RedisURI.Builder builder = RedisURI.Builder.redis(hostname, getPortNumber(port)).withSsl(getSSL(ssl)).withTimeout(getTimeout(timeout), TimeUnit.MILLISECONDS);
        if ((null != password) && (!password.isEmpty())) {
            builder.withPassword(password);
        }
        return builder.build();
    }

    protected static RedisURI produceURI(StoreParams params) {
        return produceURI(params.get(DatabaseConstants.REDIS_HOSTNAME), params.get(DatabaseConstants.REDIS_PORT), params.get(DatabaseConstants.REDIS_SSL), params.get(DatabaseConstants.REDIS_PASSWORD), params.get(DatabaseConstants.REDIS_TIMEOUT));
    }

    public void initializeRedisClient(StoreParams params) {
        if (redisClient != null) {
            return;
        }
        redisClient = RedisClient.create(produceURI(params));
    }

    public void initializeRedisClusterClient(StoreParams params) {
        if (redisClusterClient != null) {
            return;
        }
        redisClusterClient = RedisClusterClient.create(produceURI(params));
        ClusterTopologyRefreshOptions topologyRefreshOptions = ClusterTopologyRefreshOptions.builder().enablePeriodicRefresh(true).enableAllAdaptiveRefreshTriggers().dynamicRefreshSources(true).closeStaleConnections(true).build();
        redisClusterClient.setOptions(ClusterClientOptions.builder().validateClusterNodeMembership(true).maxRedirects(5).topologyRefreshOptions(topologyRefreshOptions).build());
    }

    public RedisClient getRedisClient() {
        return redisClient;
    }

    public RedisClusterClient getRedisClusterClient() {
        return redisClusterClient;
    }

    public void destroy() {
        if (redisClient != null) {
            redisClient.shutdown();
            redisClient = null;
        }
        if (redisClusterClient != null) {
            redisClusterClient.shutdown();
            redisClusterClient = null;
        }
    }
}
